##STEP 1
#read in the data from floatplanedata3.csv which was imported using RStudio's dataset import feature
data <- `floatplanedata3`
J <- nrow(data)
n <- data$pilots
y <- data$epilots

##STEP2#  run Stan
library(rstan)
rstan_options(auto_write = TRUE)
options(mc.cores = parallel::detectCores())
datafit1 <- stan(file="E:/GoogleDrive/CURRENT_PROJECTS/1_AKSSF_Salmon_Elodea/ANALYSIS/2_vector/vector_analysis/analysis092016/beta-bin092016.stan",data=c("J","y","n"), iter = 10000, chains = 4,control=list(adapt_delta=0.8, max_treedepth =10))

