# a short cut analysis using inverse-variance weighted rates

#read in the data
data <- `floatplanedata3`
nj <- data$pilots
yj <- data$epilots

#Q1: 
MLEp <- yj/nj
MLEsd <- sqrt(MLEp*(1-MLEp)/nj)
MLEp
MLEsd

#Q2:
emp_p <- mean(MLEp)
emp_sd <- sd(MLEp)
emp_p
emp_sd


aplusb <- emp_p*(1-emp_p)/(emp_sd*emp_sd)-1
alpha <- aplusb*emp_p
beta <- aplusb*(1-emp_p)
alpha
beta
check <- alpha/(alpha+beta)
check

#Q3:
inv_wt_p <- (MLEp/(MLEsd^2)+emp_p/(emp_sd^2))/(1/(MLEsd^2)+1/(emp_sd^2))
inv_wt_p


Figure2.9 <-  ggplot(sdata, aes(x=jMLEp,y=inv_wt_p)) +
  xlab("observed pilot ratio")+ ylab("inverse variance weighted pilot ratio") +
  ylim(0,1)+ xlim(0,1) + geom_jitter(height=0,width=0.1)+ geom_abline(intercept = 0, slope = 1,colour='red')+
  theme(axis.title.x = element_text(face="bold", colour="#000000", size=15),
        axis.text.x  = element_text(face="bold", angle=0, vjust=0.5, size=15)) +
  theme(axis.title.y = element_text(face="bold", colour="#000000", size=15),
        axis.text.y  = element_text(face="bold", angle=360, vjust=0.5, size=15))+
  theme(panel.background = element_rect(fill = 'white', colour="black"))
